import React, { useState } from 'react';
import './PanelEvents.css';
import Modal from '../Modal_New_Event/ModalNewEvent'; // Ajusta la ruta según tu estructura de archivos

const EventPanel = ({ isOpen, onClose, selectedDate }) => {
    const [modalOpen, setModalOpen] = useState(false);
    const [numPonentes, setNumPonentes] = useState(1);
    const [isChecked, setIsChecked] = useState({
        screen: false,
        tripie: false,
        microfonos: false,
        proyectores: false,
        bocina: false,
        eqconfe: false,
        extension: false,
        laptop: false
    });

    function handleCheckboxChange(event) {
        const { name, checked } = event.target;
        setIsChecked(prevState => ({
            ...prevState,
            [name]: checked
        }));
    }
    const openModal = () => {
        setModalOpen(true);
    };
    const closeModal = () => {
        setModalOpen(false);
    };

    const handleNumPonentesChange = (e) => {
        setNumPonentes(parseInt(e.target.value));
    };

    const generateSpeakerFields = () => {
        const fields = [];
        for (let i = 0; i < numPonentes; i++) {
            fields.push(
                <div key={i} className="speaker-field">
                    <div className="add-event-input">
                        <input type="text" id={`cargoPonente${i + 1}`} placeholder="Cargo" className="event-manager-name" name={`cargoPonente${i + 1}`} required />
                    </div>
                    <div style={{ width: "25px" }}></div>
                    <div className="add-event-input">
                        <input type="text" placeholder="Nombre" id={`nombrePonente${i + 1}`} className="event-manager-name" name={`nombrePonente${i + 1}`} required />
                    </div>
                </div>
            );
        }
        return fields;
    };


    return (
        <div className={`event-panel`}>
            <Modal isOpen={modalOpen} onClose={closeModal}>
                <div className="add-event-input">
                    <input type="text" placeholder="Nombre Del Evento" className="event-name" />
                </div>
                <div className="add-event-input">
                    <input type="text" placeholder="Nombre Del Encargado del Evento" className="event-manager-name" />
                </div>
                <div className='time_start_finish'>
                    <div className="add-event-input">
                        <input type="text" placeholder="Hora de Inicio Estimada" className="event-time-from" />
                    </div>
                    <div style={{ width: "30%" }}></div>
                    <div className="add-event-input">
                        <input type="text" placeholder="Hora de Fin Estimada" className="event-time-to" />
                    </div>
                </div>
                <div>
                    <label id=''>De los siguientes servicios selecciona los que necesites:</label>
                <div className="checklist">
                    <div><input type="checkbox" id="screen" name="screen" value="Pantallas" checked={isChecked.screen} onChange={handleCheckboxChange} />   Pantallas</div>
                    <div><input type="checkbox" id="tripie" name="tripie" value="Tripié" checked={isChecked.tripie} onChange={handleCheckboxChange} />   Tripié</div>
                    <div><input type="checkbox" id="microfonos" name="microfonos" value="Microfonos" checked={isChecked.microfonos} onChange={handleCheckboxChange} />   Microfonos</div>
                    <div><input type="checkbox" id="proyectores" name="proyectores" value="Proyectores" checked={isChecked.proyectores} onChange={handleCheckboxChange} />   Proyectores</div>
                    <div><input type="checkbox" id="bocina" name="bocina" value="Bocina" checked={isChecked.bocina} onChange={handleCheckboxChange} />   Bocina</div>
                    <div><input type="checkbox" id="eqconfe" name="eqconfe" value="Equipo de Videoconferencia" checked={isChecked.eqconfe} onChange={handleCheckboxChange} />   Equipo de Videoconferencia</div>
                    <div><input type="checkbox" id="extension" name="extension" value="Extensión" checked={isChecked.extension} onChange={handleCheckboxChange} />   Extensión</div>
                    <div><input type="checkbox" id="laptop" name="laptop" value="Laptop" checked={isChecked.laptop} onChange={handleCheckboxChange} />   Laptop</div>
                </div>
                </div>
                <div className="num_persons">
                    <label htmlFor="numPonentes">¿Cuántos Ponentes Asistirán?:</label>
                    <select name="numPonentes" id="numPonentes" onChange={handleNumPonentesChange}>
                        <option value="1">1</option>
                        <option value="2">2</option>
                        <option value="3">3</option>
                        <option value="4">4</option>
                        <option value="5">5</option>
                        <option value="6">6</option>
                        <option value="7">7</option>
                        <option value="8">8</option>
                        <option value="9">9</option>
                        <option value="10">10</option>
                        {/* Agrega más opciones según tus necesidades */}
                    </select>
                </div>
                {/* Genera los campos de ponentes dinámicamente */}
                <div id="speakerFieldsContainer">
                    {generateSpeakerFields()}
                </div>
            </Modal>
            <div id='selected_date'>{selectedDate}</div>
            <div id='registered_events'>Eventos Registrados</div>
            <div className='events_container'>
                <div id='cards_events'>Sin Eventos Registrados</div>
            </div>
            <button id='btn_new_event' onClick={openModal}>Registrar Evento</button>
        </div>
    );
}

export default EventPanel;
