import React, { useState, useRef, useEffect } from 'react';
import FullCalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import EventPanel from '../Panel_events/PanelEvents';
import './Calendar.css'

function Calendar() {
    const [panelOpen, setPanelOpen] = useState(true); // Cambiado a true para que el panel esté siempre abierto
    const [selectedDate, setSelectedDate] = useState(null);
    const calendarRef = useRef(null);

    useEffect(() => {
        const currentDate = new Date();
        const formattedDate = currentDate.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
    }, []); // Se ejecuta solo una vez al montar el componente

    const handleDateClick = (arg) => {
        const date = new Date(arg.date);
        const formattedDate = date.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
        setPanelOpen(true);
    };

    const closePanel = () => {
        setPanelOpen(false);
    };

    const endMonth = (selectInfo) => {
        console.log('Fecha seleccionada:', selectInfo.startStr, ' - ', selectInfo.endStr);
        const calendarApi = calendarRef.current.getApi();
        calendarApi.gotoDate(selectInfo.startStr);
    };

    return (
        <div className='panelsContainer'>
            <div className='bodyCalendar' >
                <FullCalendar
                    ref={calendarRef}
                    plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView={"dayGridMonth"}
                    headerToolbar={{
                        start: "today prev,next",
                        center: "title",
                        end: "dayGridMonth,timeGridWeek,timeGridDay",
                    }}
                    locales={['es']}
                    locale="es"
                    height={'90vh'}
                    radius={'24px'}
                    selectable={true}
                    dateClick={handleDateClick}
                    select={endMonth}
                    fixedWeekCount={false}
                    events={[
                        {
                            id: 'a',
                            title: 'Dia de las Madres',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'b',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'c',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'd',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'E',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-25',
                            display: 'list-item'
                        },
                        {
                            id: 'G',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-25',
                            display: 'list-item'
                        },
                        // Agregar más eventos aquí según sea necesario
                    ]}
                    displayEventTime={false}
                />
            </div>
            <div style={{ flex: panelOpen ? '1' : '0', overflowY: 'auto' }}>
                <EventPanel isOpen={panelOpen} onClose={closePanel} selectedDate={selectedDate} />
            </div>
        </div>
    );
}

export default Calendar;
