import React from 'react'
import Fullcalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";



function Calendar() {
    const handleDateSelect = (selectInfo) => {
        // Obtener la fecha seleccionada
        const selectedDate = new Date(selectInfo.startStr);

        // Obtener el mes actual y el mes seleccionado
        const currentMonth = new Date(selectInfo.view.currentStart);
        const selectedMonth = new Date(selectedDate.getFullYear(), selectedDate.getMonth(), 1);

        // Comprobar si la fecha seleccionada está en el mes siguiente o anterior
        if (selectedMonth < currentMonth) {
            selectInfo.view.calendar.prev();
        } else if (selectedMonth > currentMonth) {
            selectInfo.view.calendar.next();
        }
    };
    return (
        <div>
            <Fullcalendar
                plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                initialView={"dayGridMonth"}
                headerToolbar={{
                    start: "today prev,next",
                    center: "title",
                    end: "dayGridMonth,timeGridWeek,timeGridDay",
                }}
                locales={['es']}
                locale="es"
                height={'90vh'}
                selectable={true}
                select={handleDateSelect}
                fixedWeekCount={false}
            />
        </div>
    )
}

export default Calendar
