import React from 'react'
import Fullcalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";



function Calendar() {
    const handleDateSelect = (selectInfo) => {
        console.log('Fecha seleccionada:', selectInfo.startStr, ' - ', selectInfo.endStr);
        // Aquí puedes manejar la selección de días, por ejemplo, guardarla en el estado de tu componente.
    };
    const handleNavLinkDayClick = (date, jsEvent, view) => {
        // Obtener el día del mes actual y el día del mes del día clickeado
        const currentMonthDay = new Date(view.currentStart);
        const clickedDay = new Date(date);

        // Comparar si el día clickeado está en el mes siguiente
        if (clickedDay.getMonth() === currentMonthDay.getMonth() + 1) {
            // Cambiar al mes siguiente
            view.calendar.next();
        }
    };
    return (
        <div>
            <Fullcalendar
                plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                initialView={"dayGridMonth"}
                headerToolbar={{
                    start: "today prev,next",
                    center: "title",
                    end: "dayGridMonth,timeGridWeek,timeGridDay",
                }}
                locales={['es']}
                locale="es"
                height={'90vh'}
                selectable={true}
                fixedWeekCount={false}
                navLinkDayClick={handleNavLinkDayClick}

            />
        </div>
    )
}

export default Calendar
