// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getFirestore } from "firebase/firestore";
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
    apiKey: "AIzaSyBRhiWKyJ0tGhsLPRBXGW4TBRALbtPVH0U",
    authDomain: "uvi-eventos-3edef.firebaseapp.com",
    databaseURL: "https://uvi-eventos-3edef-default-rtdb.firebaseio.com",
    projectId: "uvi-eventos-3edef",
    storageBucket: "uvi-eventos-3edef.appspot.com",
    messagingSenderId: "824370590437",
    appId: "1:824370590437:web:13eeb612abbe1533ec5a0c",
    measurementId: "G-0TCV2R35FY"
};
const obtenerDocumentos = async (coleccion) => {
    try {
        // Obtiene una referencia a la colecci贸n
        const colRef = collection(db, coleccion);
        // Obtiene todos los documentos de la colecci贸n
        const querySnapshot = await getDocs(colRef);
        
        // Crea un array para almacenar los documentos
        const documentos = [];
        
        // Itera sobre cada documento en el snapshot
        querySnapshot.forEach((doc) => {
            // Agrega el documento al array
            documentos.push({ id: doc.id, ...doc.data() });
        });

        // Devuelve el array de documentos
        return documentos;
    } catch (error) {
        console.error("Error al obtener documentos:", error);
        throw error;
    }
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const db = getFirestore(app);
export default db;
