// En Calendar.js

import React, { useState, useRef } from 'react';
import FullCalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import EventPanel from '../Panel_events/PanelEvents';
import './Calendar.css';

function Calendar() {
    const [panelOpen, setPanelOpen] = useState(true); // Cambiado a true para que el panel esté siempre abierto
    const [selectedDate, setSelectedDate] = useState(null);
    const calendarRef = useRef(null);

    const handleDateClick = (arg) => {
        const date = new Date(arg.date);
        const formattedDate = date.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
        setPanelOpen(true);
    };

    const closePanel = () => {
        setPanelOpen(false);
    };

    return (
        <div className='panelsContainer' style={{ display: 'flex' }}>
            <div className='bodyCalendar' style={{ flex: panelOpen ? '0 0 70%' : '1' }}>
                <FullCalendar
                    ref={calendarRef}
                    plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView={"dayGridMonth"}
                    headerToolbar={{
                        start: "today prev,next",
                        center: "title",
                        end: "dayGridMonth,timeGridWeek,timeGridDay",
                    }}
                    locales={['es']}
                    locale="es"
                    height={'94vh'}
                    selectable={true}
                    dateClick={handleDateClick}
                    fixedWeekCount={false}
                    events={[
                        {
                            id: 'a',
                            title: 'Dia de las Madres',
                            start: '2024-05-10'
                        },
                        {
                            id: 'b',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-15'
                        },
                        // Agregar más eventos aquí según sea necesario
                    ]}
                    displayEventTime={false}
                    eventContent={renderEventContent}
                />
            </div>
            <div style={{ flex: panelOpen ? '1' : '0', overflowY: 'auto' }}>
                <EventPanel isOpen={panelOpen} onClose={closePanel} selectedDate={selectedDate} />
            </div>
        </div>
    );
}

function renderEventContent(eventInfo) {
    return (
        <>
            <div className="fc-content" style={{ height: '100%' }}>
                <div className="fc-title">{eventInfo.event.title}</div>
            </div>
        </>
    );
}

export default Calendar;
