// ModalEvento.js

import React from 'react';
import Modal from 'react-bootstrap/Modal';
import './styles.css';

const ModalEvento = ({ evento, isOpen, onClose }) => {
    // Agregar una validación para verificar si evento existe antes de acceder a sus propiedades
    if (!evento) {
        return null; // Si no hay evento, simplemente no renderizar el modal
    }

    return (
        <Modal show={isOpen} onHide={onClose}>
            <Modal.Header closeButton>
                <Modal.Title>{evento.title}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <p>Horario: {evento.extendedProps.timeStart} - {evento.extendedProps.timeEnd}</p>
                {/* Agrega aquí más detalles del evento si los necesitas */}
            </Modal.Body>
        </Modal>
    );
}

export default ModalEvento;
