import React, { useState, useRef, useEffect } from 'react';
import FullCalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import EventPanel from '../Panel_events/PanelEvents';
import './Calendar.css';

function Calendar() {
    const [panelOpen, setPanelOpen] = useState(true);
    const [selectedDate, setSelectedDate] = useState(null);
    const calendarRef = useRef(null);

    useEffect(() => {
        const currentDate = new Date();
        const formattedDate = currentDate.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
    }, []);

    const handleDateClick = (arg) => {
        const date = new Date(arg.date);
        const formattedDate = date.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
        setPanelOpen(true);
    };

    const closePanel = () => {
        setPanelOpen(false);
    };

    const endMonth = (selectInfo) => {
        const calendarApi = calendarRef.current.getApi();
        calendarApi.gotoDate(selectInfo.startStr);
    };

    const todayCellStyle = (arg) => {
        if (arg.date.getDate() === new Date().getDate()) {
            return { backgroundColor: 'white' };
        }
        return {};
    };

    const selectedCellStyle = (arg) => {
        const selectedDateObj = new Date(selectedDate);
        if (arg.date.getDate() === selectedDateObj.getDate() && arg.date.getMonth() === selectedDateObj.getMonth() && arg.date.getFullYear() === selectedDateObj.getFullYear()) {
            return { backgroundColor: 'green', color: 'white' };
        }
        return {};
    };

    return (
        <div className='panelsContainer'>
            <div className='bodyCalendar' >
                <FullCalendar
                    ref={calendarRef}
                    plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView={"dayGridMonth"}
                    headerToolbar={{
                        start: "today prev,next",
                        center: "title",
                        end: "dayGridMonth,timeGridWeek,timeGridDay",
                    }}
                    locales={['es']}
                    locale="es"
                    height={'90vh'}
                    selectable={true}
                    dateClick={handleDateClick}
                    select={endMonth}
                    fixedWeekCount={false}
                    events={[
                        {
                            id: 'a',
                            title: 'Dia de las Madres',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'b',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'c',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'd',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-10',
                            display: 'list-item'
                        },
                        {
                            id: 'E',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-25',
                            display: 'list-item'
                        },
                        {
                            id: 'G',
                            title: 'Fiesta de cumpleaños',
                            start: '2024-05-25',
                            display: 'list-item'
                        },
                    ]}
                    displayEventTime={false}
                    dayCellContent={todayCellStyle}
                    dayCellContent={selectedCellStyle}
                />
            </div>
            <div style={{ flex: panelOpen ? '1' : '0', overflowY: 'auto' }}>
                <EventPanel isOpen={panelOpen} onClose={closePanel} selectedDate={selectedDate} />
            </div>
        </div>
    );
}

export default Calendar;
