import React, { useState, useRef } from 'react';
import FullCalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import EventPanel from './Panel_events/PanelEvents';
import './Calendar.css'; // Archivo de estilos CSS personalizados

function Calendar() {
    const [panelOpen, setPanelOpen] = useState(false);
    const [selectedDate, setSelectedDate] = useState(null);
    const calendarRef = useRef(null);

    const handleDateClick = (arg) => {
        setSelectedDate(arg.dateStr);
        setPanelOpen(true);
    };

    const closePanel = () => {
        setPanelOpen(false);
    };

    const endMonth = (selectInfo) => {
        console.log('Fecha seleccionada:', selectInfo.startStr, ' - ', selectInfo.endStr);
        const calendarApi = calendarRef.current.getApi();
        calendarApi.gotoDate(selectInfo.startStr);
    };

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ flex: panelOpen ? '0 0 70%' : '1' }}>
                <FullCalendar
                    ref={calendarRef}
                    plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView={"dayGridMonth"}
                    headerToolbar={{
                        start: "today prev,next",
                        center: "title",
                        end: "dayGridMonth,timeGridWeek,timeGridDay",
                    }}
                    locales={['es']}
                    locale="es"
                    height={'90vh'}
                    selectable={true}
                    dateClick={handleDateClick}
                    select={endMonth}
                    fixedWeekCount={false}
                    events={[
                        // Evento para resaltar la fecha actual
                        { 
                            start: new Date().toISOString().slice(0,10), // Fecha actual
                            rendering: 'background',
                            color: '#ffcc00' // Color de resaltado para la fecha actual
                        },
                        // Evento para resaltar la fecha seleccionada
                        selectedDate && { 
                            start: selectedDate,
                            end: selectedDate,
                            rendering: 'background',
                            color: '#3399ff' // Color de resaltado para la fecha seleccionada
                        }
                    ]}
                />
            </div>
            <div style={{ flex: panelOpen ? '1' : '0', overflowY: 'auto' }}>
                <EventPanel isOpen={panelOpen} onClose={closePanel} selectedDate={selectedDate} />
            </div>
        </div>
    );
}

export default Calendar;
