import React, { useState, useRef, useEffect } from 'react';
import FullCalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import EventPanel from '../Panel_events/PanelEvents';
import './Calendar.css';
import esLocale from '@fullcalendar/core/locales/es';
import { obtenerDocumentos } from '../../firebase'; // Importa la función para obtener documentos desde Firebase

function Calendar() {
    const [panelOpen, setPanelOpen] = useState(true);
    const [selectedDate, setSelectedDate] = useState(null);
    const [currentDate] = useState(new Date());
    const [events, setEvents] = useState([]); // Estado para almacenar los eventos
    const calendarRef = useRef(null);
    const [currentCell, setCurrentCell] = useState(null);

    useEffect(() => {
        const formattedDate = currentDate.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
        obtenerYAsignarEventos(); // Llama a la función para obtener eventos cuando el componente se monta
    }, [currentDate]);

    const obtenerYAsignarEventos = async () => {
        try {
            // Obtener eventos desde Firebase
            const documentos = await obtenerDocumentos("events");
            // Convertir el formato de eventos si es necesario
            const eventosFormateados = documentos.map(documento => ({
                title: documento.eventName,
                start: documento.eventTimeFrom,
                end: documento.eventTimeTo
                // Puedes añadir más propiedades según lo necesites
            }));
            // Asignar los eventos al estado
            setEvents(eventosFormateados);
        } catch (error) {
            console.error("Error al obtener eventos:", error);
        }
    };

    const handleDateClick = (arg) => {
        const date = new Date(arg.date);
        const formattedDate = date.toLocaleDateString('es-ES', { day: '2-digit', month: 'long', year: 'numeric' });
        setSelectedDate(formattedDate);
        setPanelOpen(true);
        if (currentCell) {
            currentCell.style.backgroundColor = ''; // Restablece el color de la celda actual
            currentCell.style.color = 'black'
        }
        setCurrentCell(arg.dayEl); // Almacena la celda actual
        arg.dayEl.style.backgroundColor = '#28AD56'; // Cambia el color de la celda presionada a verde
        arg.dayEl.style.colorr = '#fffff';
    };

    const closePanel = () => {
        setPanelOpen(false);
    };

    const endMonth = (selectInfo) => {
        const calendarApi = calendarRef.current.getApi();
        calendarApi.gotoDate(selectInfo.startStr);
    };

    return (
        <div className='panelsContainer'>
            <div className='bodyCalendar' >
                <FullCalendar
                    ref={calendarRef}
                    plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin]}
                    initialView={"dayGridMonth"}
                    headerToolbar={{
                        start: "today prev,next",
                        center: "title",
                        end: "dayGridMonth,timeGridWeek,timeGridDay",
                    }}
                    locales={['es']}
                    locale={esLocale}
                    height={'90vh'}
                    lang= "es"
                    selectable={true}
                    dateClick={handleDateClick}
                    select={endMonth}
                    fixedWeekCount={false}
                    events={events} // Pasa los eventos al componente FullCalendar
                    displayEventTime={false}
                />
            </div>
            <div style={{ flex: panelOpen ? '1' : '0', overflowY: 'auto' }}>
                <EventPanel isOpen={panelOpen} onClose={closePanel} selectedDate={selectedDate} />
            </div>
        </div>
    );
}

export default Calendar;
