import React, { useState, useRef, useEffect } from 'react';
import FullCalendar from "@fullcalendar/react";
import DayGridPlugin from "@fullcalendar/daygrid";
import timeGridPlugin from "@fullcalendar/timegrid";
import interactionPlugin from "@fullcalendar/interaction";
import EventPanel from '../Panel_events/PanelEvents';
import bootstrap5Plugin from '@fullcalendar/bootstrap5'; // Importa el plugin de Bootstrap 5 para FullCalendar

// Importa los estilos de Bootstrap
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

function Calendar() {
    const [panelOpen, setPanelOpen] = useState(false);
    const [selectedDate, setSelectedDate] = useState(null);
    const calendarRef = useRef(null);

    useEffect(() => {
        // Sobrescribe los estilos de la fecha actual
        const styleTag = document.createElement('style');
        styleTag.innerHTML = `
            .fc-highlight-today {
                background-color: green !important;
                border-color: green !important;
            }
        `;
        document.head.appendChild(styleTag);

        return () => {
            document.head.removeChild(styleTag);
        };
    }, []);

    const handleDateClick = (arg) => {
        setSelectedDate(arg.dateStr);
        setPanelOpen(true);
    };

    const closePanel = () => {
        setPanelOpen(false);
    };

    const endMonth = (selectInfo) => {
        console.log('Fecha seleccionada:', selectInfo.startStr, ' - ', selectInfo.endStr);
        const calendarApi = calendarRef.current.getApi();
        calendarApi.gotoDate(selectInfo.startStr);
    };

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ flex: panelOpen ? '0 0 70%' : '1' }}>
                <FullCalendar
                    ref={calendarRef}
                    plugins={[DayGridPlugin, timeGridPlugin, interactionPlugin, bootstrap5Plugin]}
                    initialView={"dayGridMonth"}
                    headerToolbar={{
                        start: "today prev,next",
                        center: "title",
                        end: "dayGridMonth,timeGridWeek,timeGridDay",
                    }}
                    locales={['es']}
                    locale="es"
                    height={'90vh'}
                    selectable={true}
                    dateClick={handleDateClick}
                    select={endMonth}
                    fixedWeekCount={false}
                    themeSystem="bootstrap5"
                    events={[
                        { title: 'event 1', date: '2024-04-01' },
                        { title: 'event 2', date: '2024-05-02' }
                    ]}
                />
            </div>
            <div style={{ flex: panelOpen ? '1' : '0', overflowY: 'auto' }}>
                <EventPanel isOpen={panelOpen} onClose={closePanel} selectedDate={selectedDate} />
            </div>
        </div>
    );
}

export default Calendar;
