import React, { useState } from 'react';
import './PanelEvents.css';
import Modal from '../Modal_New_Event/ModalNewEvent'; // Ajusta la ruta según tu estructura de archivos

const EventPanel = ({ isOpen, onClose, selectedDate }) => {
    const [modalOpen, setModalOpen] = useState(false);
    const [numPonentes, setNumPonentes] = useState(1);
    const [isChecked, setIsChecked] = useState({
        screen: false,
        tripie: false,
        microfonos: false,
        proyectores: false,
        bocina: false,
        eqconfe: false,
        extension: false,
        laptop: false
    });
    const [eventData, setEventData] = useState({
        eventName: '',
        eventManagerName: '',
        eventTimeFrom: '',
        eventTimeTo: '',
        services: [],
        numPonentes: 1
    });

    function handleCheckboxChange(event) {
        const { name, checked } = event.target;
        setIsChecked(prevState => ({
            ...prevState,
            [name]: checked
        }));
    }

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setEventData({
            ...eventData,
            [name]: value
        });
    };


    const handleSubmit = (event) => {
        event.preventDefault();
        const selectedServices = Object.entries(isChecked)
            .filter(([key, value]) => value)
            .map(([key, value]) => key);
        setEventData({
            ...eventData,
            services: selectedServices
        });
        console.log(eventData);
        // Aquí puedes enviar los datos al servidor o hacer cualquier otra cosa con ellos
    };
    const openModal = () => {
        setModalOpen(true);
    };
    const closeModal = () => {
        setModalOpen(false);
    };

    const handleNumPonentesChange = (e) => {
        setNumPonentes(parseInt(e.target.value));
    };

    const generateSpeakerFields = () => {
        const fields = [];
        for (let i = 0; i < numPonentes; i++) {
            fields.push(
                <div key={i} className="speaker-field">
                    <div className="add-event-input">
                        <input type="text" id={`cargoPonente${i + 1}`} placeholder="Cargo" className="event-manager-name" name={`cargoPonente${i + 1}`} required />
                    </div>
                    <div style={{ width: "25px" }}></div>
                    <div className="add-event-input">
                        <input type="text" placeholder="Nombre" id={`nombrePonente${i + 1}`} className="event-manager-name" name={`nombrePonente${i + 1}`} required />
                    </div>
                </div>
            );
        }
        return fields;
    };


    return (
        <div className={`event-panel`}>
            <Modal isOpen={modalOpen} onClose={closeModal}>
            <form onSubmit={handleSubmit}>

            </form>

               

            </Modal>
            <div id='selected_date'>{selectedDate}</div>
            <div id='registered_events'>Eventos Registrados</div>
            <div className='events_container'>
                <div id='cards_events'>Sin Eventos Registrados</div>
            </div>
            <button id='btn_new_event' onClick={openModal}>Registrar Evento</button>
        </div>
    );
}

export default EventPanel;
