import React from 'react';
import './PanelEvents.css';

const EventPanel = ({ isOpen, onClose, selectedDate }) => {
    return (
        <div className={`event-panel open`}> {/* Asegúrate de que siempre esté abierta */}
            <div className='events_container'>
                <p id='fecha'>{selectedDate}</p>
                <h2>Registro de Eventos</h2>

                <button className="event-panel__close-btn" onClick={onClose}>Cerrar Panel</button>
            </div>

        </div>
    );
}

export default EventPanel;
